import syssys.stdin = open('10157.txt', 'r')C, R = map(int,input().split())K = int(input())arr = [[0]*C for _ in range(R)]dx = [0, 1, 0, -1]dy = [-1, 0, 1, 0]x = 0y = R-1i = 0arr[y][x] = 1for num in range(2, (C*R)+1):        x += dx[i]        y += dy[i]        arr[y][x] = num        if 0 <= x+dx[i] < C and 0 <= y+dy[i] < R and not arr[y+dy[i]][x+dx[i]]:            continue        if i != 3:            i += 1        else:            i = 0result = []for k in range(R):    for j in range(C):        if arr[k][j] == K:            result.append(j+1)            result.append(R-k)if not result:    print(0)else:    print(*result)